var startBlock = big.NewInt(26676133)
var Endpoint = "https://bsc-dataseed.binance.org/"

func main() {
	// 连接以太坊客户端
	client, err := ethclient.Dial(Endpoint)
	if err != nil {
		log.Fatal("connect network error,", err)
	}

	// 解析合约 ABI 字符串
	abiObj, err := abi.JSON(strings.NewReader(contractABI))
	if err != nil {
		log.Fatal("get abi str error,", err)
	}

	// 获取合约事件过滤器
	query := ethereum.FilterQuery{
		FromBlock: startBlock,
		ToBlock:   nil,
		Addresses: []common.Address{
			contractAddress,
		},
	}
	logs, err := client.FilterLogs(context.Background(), query)
	if err != nil {
		log.Fatal("filter logs error,", err)
	}

	LogTransferSig := []byte("Transfer(address,address,uint256)")
	LogTransferSigHash := crypto.Keccak256Hash(LogTransferSig)

	fmt.Println("LogTransferSigHash", LogTransferSigHash.Hex())

	// 处理合约事件
	for _, vLog := range logs {
		fmt.Printf("Log Block Number: %d\n", vLog.BlockNumber)
		fmt.Printf("Log TxHash: %s\n", vLog.TxHash.Hex())
		fmt.Printf("Log Index: %d\n", vLog.Index)

		switch vLog.Topics[0].Hex() {
		case LogTransferSigHash.Hex():
			fmt.Println("Log Name: Transfer")

			var transferEvent LogTransfer

			err := abiObj.UnpackIntoInterface(&transferEvent, "Transfer", vLog.Data)
			if err != nil {
				log.Fatal("unpack abi", err)
			}

			transferEvent.From = common.HexToAddress(vLog.Topics[1].Hex())
			transferEvent.To = common.HexToAddress(vLog.Topics[2].Hex())

			fmt.Printf("From: %s\n", transferEvent.From.Hex())
			fmt.Printf("To: %s\n", transferEvent.To.Hex())
			fmt.Printf("TokenId: %s\n", transferEvent.TokenId.String())
		}
	}
}
